<template>
  <div>
    <v-row justify="space-around">
      <v-col cols="4" v-if="doShowMyLocation">
        <v-list two-line subheader>
          <v-subheader>{{ $t('My Locations') }}</v-subheader>
          <v-list-item href="javascript:;" v-for="item in knownLocations" v-bind:key="item.id" @click.native.stop="selectKnownLocation(item)" :style="(item && knownLocationMode && selectedKnownLocation && item.id === selectedKnownLocation.id) ? 'background-color: #455a64' : ''">
            <v-list-item-icon>
              <v-icon>mdi-map-marker</v-icon>
            </v-list-item-icon>
            <v-list-item-content>
              <v-list-item-title>{{ item.short_name }}</v-list-item-title>
              <v-list-item-subtitle>{{ item.country }}</v-list-item-subtitle>
            </v-list-item-content>
          </v-list-item>
        </v-list>
      </v-col>
      <v-col cols="doShowMyLocation ? 8 : 12" >
        <v-card class="blue-grey darken-2 white--text">
          <v-card-title primary-title>
            <v-container fluid>
              <v-row>
                <v-col>
                  <div>
                    <div class="text-h5" style="overflow: hidden; white-space: nowrap; text-overflow: ellipsis;">{{ locationForDetail ? locationForDetail.short_name + ', ' + locationForDetail.country :  '-' }}</div>
                    <v-btn @click.native.stop="useLocation()" style="position: absolute; right: 20px"><v-icon>mdi-chevron-right</v-icon> {{ $t('Use this location') }}</v-btn>
                    <div class="grey--text text-subtitle-2" v-if="locationForDetail.street_address">{{ locationForDetail ? (locationForDetail.street_address ? locationForDetail.street_address : $t('Unknown Address')) : '-' }}</div>
                    <div class="grey--text text-subtitle-2">{{ locationForDetail ? locationForDetail.lat.toFixed(5) + ' ' + locationForDetail.lng.toFixed(5) : '-' }}</div>
                    <div class="grey--text text-subtitle-2 drag-coordinates" v-if="isDragging && dragCoordinates">
                      <span class="drag-hint">
                        <v-icon small color="orange">mdi-drag</v-icon> 
                        拖拽中: {{ dragCoordinates.lat.toFixed(5) + ', ' + dragCoordinates.lng.toFixed(5) }}
                      </span>
                    </div>
                  </div>
                </v-col>
              </v-row>
            </v-container>
          </v-card-title>
          <div style="height: 375px">
              <!-- <v-btn light fab class="mx-0 pa-0" @click.native.stop="centerOnRealPosition()" style="position: absolute; z-index: 10000; bottom: 16px; right: 12px;">
                <v-icon>mdi-crosshairs-gps</v-icon>
              </v-btn> -->
            <l-map class="black--text" ref="myMap" :center="mapCenter" :zoom="defaultZoom" style="width: 100%; height: 375px;" :options="{zoomControl: false, minZoom: effectiveMinZoom, maxZoom: effectiveMaxZoom}">
              <l-control-zoom position="topright"></l-control-zoom>
              <l-tile-layer :url="url" attribution='&copy; <a target="_blank" rel="noopener" href="http://osm.org/copyright">OpenStreetMap</a> contributors'></l-tile-layer>
              <l-marker :key="loc.id"
                  v-for="loc in knownLocations"
                  :lat-lng="[ loc.lat, loc.lng ]"
                  :clickable="true"
                  :opacity="(!pickLocationMode && selectedKnownLocation && selectedKnownLocation === loc ? 1.0 : 0.25)"
                  @click="selectKnownLocation(loc)"
                  :draggable="!pickLocationMode && selectedKnownLocation && selectedKnownLocation === loc" 
                  @dragstart="startDrag"
                  @drag="onDrag"
                  @dragend="dragEnd"
                >
                  <l-tooltip v-if="!pickLocationMode && selectedKnownLocation && selectedKnownLocation === loc && isDragging">
                    <div class="black--text">
                      <strong>拖拽移动中</strong><br>
                      纬度: {{ dragCoordinates ? dragCoordinates.lat.toFixed(6) : loc.lat.toFixed(6) }}<br>
                      经度: {{ dragCoordinates ? dragCoordinates.lng.toFixed(6) : loc.lng.toFixed(6) }}<br>
                      <small style="color: #666;">松开鼠标确认位置</small>
                    </div>
                  </l-tooltip>
                </l-marker>
              <l-circle v-if="startLocation"
                :lat-lng="[ startLocation.lat, startLocation.lng ]"
                :radius="startLocation.accuracy"
                :options="{
                  strokeColor: '#0000FF',
                  strokeOpacity: 0.5,
                  strokeWeight: 1,
                  fillColor: '#0000FF',
                  fillOpacity: 0.08}"></l-circle>
              <l-marker v-if="pickLocationMode && pickLocation" 
                :lat-lng="[ pickLocation.lat, pickLocation.lng ]"
                :draggable="true" 
                @dragstart="startDrag"
                @drag="onDrag"
                @dragend="dragEnd">
                <l-tooltip>
                  <div class="black--text">
                    <span v-if="!isDragging">拖拽调整位置</span>
                    <div v-if="isDragging">
                      <strong>拖拽移动中</strong><br>
                      纬度: {{ dragCoordinates ? dragCoordinates.lat.toFixed(6) : pickLocation.lat.toFixed(6) }}<br>
                      经度: {{ dragCoordinates ? dragCoordinates.lng.toFixed(6) : pickLocation.lng.toFixed(6) }}<br>
                      <small style="color: #666;">松开鼠标确认位置</small>
                    </div>
                  </div>
                </l-tooltip>
              </l-marker>
            </l-map>
          </div>
        </v-card>
      </v-col>
    </v-row>
  </div>
</template>

<script>
import swh from '@/assets/sw_helpers.js'
import { LMap, LTileLayer, LMarker, LCircle, LTooltip, LControlZoom } from 'vue2-leaflet'
import L from 'leaflet'
import MapConfig from '@/config/map-config.js'

export default {
  data: function () {
    return {
      mode: 'pick',
      pickLocation: undefined,
      selectedKnownLocation: undefined,
      mapCenter: [43.6, 1.4333],
      url: process.env.NODE_ENV === 'production' ? '/tiles/{z}/{x}/{y}.png' : 'http://localhost:8080/tiles/{z}/{x}/{y}.png',
      fallbackUrl: '/tiles/{z}/{x}/{y}.png',
      serverUrl: 'http://localhost:8080/tiles/{z}/{x}/{y}.png',
      onlineUrl: 'https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png',
      // 使用配置文件中的缩放级别设置
      minZoom: MapConfig.zoom.offline.min,
      maxZoom: MapConfig.zoom.offline.max,
      defaultZoom: MapConfig.zoom.offline.default,
      // 拖拽状态管理
      isDragging: false,
      dragCoordinates: null
    }
  },
  props: ['showMyLocation', 'knownLocations', 'startLocation', 'realLocation'],
  computed: {
    doShowMyLocation: function () {
      return this.showMyLocation === undefined ? false : this.showMyLocation
    },
    pickLocationMode: function () {
      return this.mode === 'pick'
    },
    knownLocationMode: function () {
      return this.mode === 'known'
    },
    locationForDetail: function () {
      if (this.pickLocationMode && this.pickLocation === undefined) {
        return this.startLocation
      }
      return this.pickLocationMode ? this.pickLocation : this.selectedKnownLocation
    },
    // 动态缩放级别配置
    effectiveMinZoom: function () {
      // 在生产环境下使用离线瓦片的限制，开发环境可以更宽松
      const offlineMin = MapConfig.zoom.offline.min
      const onlineMin = MapConfig.zoom.online.min
      return process.env.NODE_ENV === 'production' ? offlineMin : Math.max(onlineMin, offlineMin - 1)
    },
    effectiveMaxZoom: function () {
      // 在生产环境下严格限制，开发环境允许更高级别（在线瓦片）
      const offlineMax = MapConfig.zoom.offline.max
      const onlineMax = MapConfig.zoom.online.max
      return process.env.NODE_ENV === 'production' ? offlineMax : Math.min(onlineMax, offlineMax + 6)
    }
  },
  watch: {
    startLocation: function () {
      this.setPickLocation(this.startLocation)
    }
  },
  mounted: function () {
    const that = this
    this.setPickLocation(this.startLocation)

    // 检测瓦片服务器可用性
    this.checkTileAvailability()

    this.$nextTick(() => {
      const map = this.$refs.myMap.mapObject
      map._onResize()

      // 设置缩放限制
      this.updateZoomLimits(map)

      // 添加离线搜索控件
      this.addOfflineSearchControl(map)
    })
  },
  methods: {
    selectKnownLocation: function (loc) {
      this.selectedKnownLocation = loc
      this.setKnownLocationMode()
      this.mapCenter = [loc.lat, loc.lng]
    },
    useLocation: function () {
      this.$emit('locationSelected', this.locationForDetail)
      this.$emit('resetLocation', this.locationForDetail.lat, this.locationForDetail.lng)
    },
    setPickLocationMode: function () {
      this.mode = 'pick'
    },
    setKnownLocationMode: function () {
      this.mode = 'known'
    },
    setPickLocation: function (loc) {
      if (loc.accuracy < 100) {
        for (const l of this.knownLocations) {
          const d = swh.getDistanceFromLatLonInM(l.lat, l.lng, loc.lat, loc.lng)
          if (d < 100) {
            this.selectKnownLocation(l)
            return
          }
        }
      }
      var pos = { lat: loc.lat, lng: loc.lng }
      this.mapCenter = [pos.lat, pos.lng]
      this.pickLocation = loc
      this.setPickLocationMode()
    },
    // Called when the user clicks on the small cross button
    centerOnRealPosition: function () {
      this.setPickLocation(this.realLocation)
    },
    // 拖拽开始事件
    startDrag: function (event) {
      this.isDragging = true
      this.dragCoordinates = {
        lat: event.target._latlng.lat,
        lng: event.target._latlng.lng
      }
      console.log('开始拖拽，初始坐标:', this.dragCoordinates)
    },
    // 拖拽过程中的事件
    onDrag: function (event) {
      this.dragCoordinates = {
        lat: event.target._latlng.lat,
        lng: event.target._latlng.lng
      }
      // 实时更新坐标显示，但不频繁打印日志
      if (Math.random() < 0.1) { // 只有10%的概率打印，避免日志过多
        console.log('拖拽中，当前坐标:', this.dragCoordinates.lat.toFixed(6), this.dragCoordinates.lng.toFixed(6))
      }
    },
    // 拖拽结束事件
    dragEnd: function (event) {
      var that = this
      var pos = { lat: event.target._latlng.lat, lng: event.target._latlng.lng, accuracy: 0 }

      // 更新最终坐标
      this.dragCoordinates = {
        lat: pos.lat,
        lng: pos.lng
      }

      console.log('拖拽结束，最终坐标: ' + pos.lat.toFixed(6) + ',' + pos.lng.toFixed(6))

      swh.geoCodePosition(pos, that).then((p) => { 
        that.pickLocation = p
        that.setPickLocationMode()
        // 拖拽完成后清除拖拽状态
        setTimeout(() => {
          that.isDragging = false
          that.dragCoordinates = null
        }, 500) // 延迟清除，让用户能看到最终坐标
      })

      this.$emit('resetLocation', pos.lat, pos.lng)
    },
    checkTileAvailability: function () {
      const that = this

      // 在生产环境下优先使用静态瓦片
      if (process.env.NODE_ENV === 'production') {
        that.testStaticTiles().then(isAvailable => {
          if (isAvailable) {
            console.log('使用静态瓦片文件')
            that.url = that.fallbackUrl
          } else {
            console.log('静态瓦片不可用，尝试tile服务器')
            that.testTileServer()
          }
        })
      } else {
        // 开发环境下优先尝试tile服务器
        that.testTileServer()
      }
    },

    testStaticTiles: function () {
      const that = this
      return new Promise((resolve) => {
        // 测试多个缩放级别的瓦片是否存在
        const testUrls = [
          '/tiles/0/0/0.png',
          '/tiles/1/0/0.png',
          '/tiles/2/1/1.png'
        ]

        let testCount = 0
        let successCount = 0

        testUrls.forEach(url => {
          fetch(url, { method: 'HEAD' })
            .then(response => {
              testCount++
              if (response.ok) {
                successCount++
              }

              if (testCount === testUrls.length) {
                // 如果至少一半的测试瓦片可用，认为静态瓦片可用
                resolve(successCount >= testUrls.length / 2)
              }
            })
            .catch(() => {
              testCount++
              if (testCount === testUrls.length) {
                resolve(successCount >= testUrls.length / 2)
              }
            })
        })

        // 超时处理
        setTimeout(() => {
          if (testCount < testUrls.length) {
            resolve(false)
          }
        }, 2000)
      })
    },

    testTileServer: function () {
      const that = this

      // 测试tile服务器是否可用
      const testUrl = this.serverUrl.replace('{z}', '0').replace('{x}', '0').replace('{y}', '0')

      fetch(testUrl, { method: 'HEAD', timeout: 3000 })
        .then(response => {
          if (response.ok) {
            console.log('tile服务器可用')
            that.url = that.serverUrl
          } else {
            throw new Error('tile服务器响应错误')
          }
        })
        .catch(error => {
          console.log('tile服务器不可用，使用静态瓦片:', error)
          that.url = that.fallbackUrl

          // 如果静态瓦片也不可用，最后尝试在线瓦片
          that.testStaticTiles().then(isAvailable => {
            if (!isAvailable && process.env.NODE_ENV === 'development') {
              console.log('切换到在线瓦片服务')
              that.url = that.onlineUrl
            }
          })
        })
    },
    addOfflineSearchControl: function (map) {
      const that = this

      // 创建离线搜索控件
      const searchControl = L.Control.extend({
        options: {
          position: 'topleft'
        },
        onAdd: function(map) {
          const container = L.DomUtil.create('div', 'leaflet-control-geocoder leaflet-bar')
          container.innerHTML = `
            <div class="leaflet-control-geocoder-form">
              <input type="text" placeholder="输入坐标 (lat,lng)" />
              <button type="button">搜索</button>
            </div>
          `

          const input = container.querySelector('input')
          const button = container.querySelector('button')

          button.onclick = function() {
            that.handleOfflineSearch(input.value)
          }

          input.onkeypress = function(e) {
            if (e.key === 'Enter') {
              that.handleOfflineSearch(input.value)
            }
          }

          return container
        }
      })

      new searchControl().addTo(map)
    },
    handleOfflineSearch: function (query) {
      // 解析坐标输入 (支持多种格式)
      const coordPattern = /(-?\d+\.?\d*)[,\s]+(-?\d+\.?\d*)/
      const match = query.match(coordPattern)

      if (match) {
        const lat = parseFloat(match[1])
        const lng = parseFloat(match[2])

        if (lat >= -90 && lat <= 90 && lng >= -180 && lng <= 180) {
          const pos = { lat: lat, lng: lng, accuracy: 0 }
          this.mapCenter = [lat, lng]

          const loc = {
            short_name: `${lat.toFixed(3)}, ${lng.toFixed(3)}`,
            country: this.$t('Unknown'),
            lng: lng,
            lat: lat,
            alt: 0,
            accuracy: 0,
            street_address: ''
          }

          this.pickLocation = loc
          this.setPickLocationMode()
        } else {
          alert('坐标范围错误：纬度(-90到90)，经度(-180到180)')
        }
      } else {
        alert('请输入有效的坐标格式：纬度,经度 (例如: 40.1, 116.2)')
      }
    },
    updateZoomLimits: function (map) {
      const that = this

      // 设置基础缩放限制
      map.setMinZoom(this.effectiveMinZoom)
      map.setMaxZoom(this.effectiveMaxZoom)

      console.log(`地图缩放限制: ${this.effectiveMinZoom} - ${this.effectiveMaxZoom}`)

      // 检测当前环境的瓦片可用性并调整限制
      if (process.env.NODE_ENV === 'production') {
        // 生产环境：严格按照瓦片数据限制
        this.detectAvailableTileLevels().then(levels => {
          if (levels.min !== null && levels.max !== null) {
            map.setMinZoom(levels.min)
            map.setMaxZoom(levels.max)
            console.log(`检测到可用瓦片级别: ${levels.min} - ${levels.max}`)
          }
        })
      }
    },

    detectAvailableTileLevels: function () {
      const that = this
      return new Promise((resolve) => {
        const testLevels = MapConfig.detection.testLevels
        let availableLevels = []
        let testCount = 0

        testLevels.forEach(level => {
          const testUrl = `/tiles/${level}/0/0.png`

          fetch(testUrl, { method: 'HEAD' })
            .then(response => {
              testCount++
              if (response.ok) {
                availableLevels.push(level)
              }

              if (testCount === testLevels.length) {
                availableLevels.sort((a, b) => a - b)
                resolve({
                  min: availableLevels.length > 0 ? availableLevels[0] : null,
                  max: availableLevels.length > 0 ? availableLevels[availableLevels.length - 1] : null,
                  available: availableLevels
                })
              }
            })
            .catch(() => {
              testCount++
              if (testCount === testLevels.length) {
                availableLevels.sort((a, b) => a - b)
                resolve({
                  min: availableLevels.length > 0 ? availableLevels[0] : null,
                  max: availableLevels.length > 0 ? availableLevels[availableLevels.length - 1] : null,
                  available: availableLevels
                })
              }
            })
        })

        // 使用配置的超时时间
        setTimeout(() => {
          if (testCount < testLevels.length) {
            resolve({ 
              min: MapConfig.zoom.offline.min, 
              max: MapConfig.zoom.offline.max, 
              available: [] 
            })
          }
        }, MapConfig.detection.timeout)
      })
    }
  },
  components: { LMap, LTileLayer, LMarker, LCircle, LTooltip, LControlZoom }
}
</script>

<style>
.leaflet-control-geocoder-form input {
  caret-color:#000 !important;
  color: #000 !important;
}

/* 拖拽坐标显示样式 */
.drag-coordinates {
  background-color: rgba(255, 152, 0, 0.1);
  border-left: 3px solid #ff9800;
  padding: 4px 8px;
  border-radius: 4px;
  margin-top: 4px;
  animation: pulse 1.5s ease-in-out infinite alternate;
}

@keyframes pulse {
  from {
    background-color: rgba(255, 152, 0, 0.1);
  }
  to {
    background-color: rgba(255, 152, 0, 0.2);
  }
}

/* Tooltip样式增强 */
.leaflet-tooltip {
  font-size: 12px !important;
  font-weight: 500 !important;
}

.leaflet-tooltip strong {
  color: #ff9800 !important;
}

.leaflet-tooltip small {
  font-style: italic;
}

/* 拖拽中的标记动画 */
.leaflet-marker-dragging {
  transition: none !important;
}

/* 拖拽提示文字样式 */
.drag-hint {
  display: inline-flex;
  align-items: center;
  gap: 4px;
  font-weight: 500;
}
</style>
